{% block titre %}
    <h2>Mes séries favorites</h2>
{% endblock %}

{% block body %}
    <h1>Toutes les séries de la table program : </h1>
    {% for program in programs %}
        <div>
            <h2>{{ loop.index }} / {{ program.title }} - Catégorie : {{ program.category.name }}</h2>
            <p>{{ program.summary }}</p>
        </div>
    {% else %}
        Aucune série trouvée.
    {% endfor %}
    <a href=\"{{ path('app_index') }}\">
        Retour à l'accueil
    </a>
{% endblock %}
